	(5, 'sc', 4, 'Set your own Goal!', 5, '<textarea class="form-control" rows="5"></textarea>', 'images/goal-ownprogram.jpg'),
	(6, 'en', 9, 'Goal 1', 1, '<ul>\r\n	<li>Include one portion of beans in your daily lunch or your main evening meal, three times per week</li>\r\n	<li>Eat two pieces of fruit a day and three portions of vegetables (= 3 heaped tablespoons)</li>\r\n</ul>', 'images/riskfactors/diet/goal.jpg'),
	(6, 'fr', 9, 'Objectif 1', 1, '<ul>\r\n   <li>Incluez une portion de haricots dans votre d&eacute;jeuner ou votre d&icirc;ner, trois fois par semaine</li>\r\n   <li>Mangez 2 fruits par jour et 3 portions de l&eacute;gumes (une portion correspond &agrave; 3 cuill&egrave;res &agrave; soupe remplies)</li>\r\n</ul>', 'images/riskfactors/diet/goal.jpg'),
	(6, 'sc', 9, 'Goal 1', 1, '<ul>\r\n	<li>Include one portion of beans in your daily lunch or your main evening meal, three times per week</li>\r\n	<li>Eat two pieces of fruit a day and three portions of vegetables (= 3 heaped tablespoons)</li>\r\n</ul>', 'images/riskfactors/diet/goal.jpg'),
	(7, 'en', 9, 'Goal 2', 2, '<ul>\r\n	<li>Eat less sugar. Processed foods tend to have added sugar, so this will be easier to do if you prepare and cook your own meals</li>\r\n</ul>\r\n\r\n<p>and/or</p>\r\n\r\n<ul>\r\n	<li>Choose food only from the Mediterranean Diet</li>\r\n	<li>Use olive oil and limit butter, margarine and cream to one serving per day</li>\r\n	<li>Eat three servings of nuts per week (1 serving = a handful)</li>\r\n</ul>', 'images/riskfactors/diet/goal.jpg'),
	(7, 'fr', 9, 'Objectif 2', 2, '<ul>\r\n   <li>Mangez moins de sucre. Les aliments transform&eacute;s ont tendance &agrave; contenir des sucres ajout&eacute;s, il est donc plus facile de pr&eacute;parer et de cuisiner vous-m&ecirc;me vos plats.</li>\r\n</ul>\r\n\r\n<p>et/ou</p>\r\n\r\n<ul>\r\n   <li>Choisissez votre nourriture seulement sur la base d&rsquo;une alimentation m&eacute;diterran&eacute;enne</li>\r\n   <li>Utilisez de l&rsquo;huile d&rsquo;olive et limitez le beurre, la margarine et la cr&egrave;me &agrave; un seul plat par jour</li>\r\n   <li>Mangez 3 portions de noix par semaine (1 portion = 1 poign&eacute;e)</li>\r\n</ul>', 'images/riskfactors/diet/goal.jpg'),
	(7, 'sc', 9, 'Goal 2', 2, '<ul>\r\n	<li>Eat less sugar. Processed foods tend to have added sugar, so this will be easier to do if you prepare and cook your own meals</li>\r\n</ul>\r\n\r\n<p>and/or</p>\r\n\r\n<ul>\r\n	<li>Choose food only from the Mediterranean Diet</li>\r\n	<li>Use olive oil and limit butter, margarine and cream to one serving per day</li>\r\n	<li>Eat three servings of nuts per week (1 serving = a handful)</li>\r\n</ul>', 'images/riskfactors/diet/goal.jpg'),
	(8, 'en', 9, 'Goal 3', 3, '<ul>\r\n	<li>Eat less red meat and replace it with oily fish or white meat twice a week</li>\r\n	<li>Replace white bread, pasta and rice with whole-grain bread, pasta and rice</li>\r\n	<li>Eat a tomato-based sauce at least twice a week</li>\r\n</ul>', 'images/riskfactors/diet/goal.jpg'),
	(8, 'fr', 9, 'Objectif 3', 3, '<ul>\r\n   <li>Mangez moins de viande rouge et remplacez-l&agrave; par du poisson gras ou de la viande blanche 2 fois par semaine</li>\r\n   <li>Remplacez le pain blanc, les p&acirc;tes et le riz par du pain, des p&acirc;tes et du riz au bl&eacute; complet</li>\r\n   <li>Mangez de la sauce tomate au moins 2 fois par semaine</li>\r\n</ul>', 'images/riskfactors/diet/goal.jpg'),
	(8, 'sc', 9, 'Goal 3', 3, '<ul>\r\n	<li>Eat less red meat and replace it with oily fish or white meat twice a week</li>\r\n	<li>Replace white bread, pasta and rice with whole-grain bread, pasta and rice</li>\r\n	<li>Eat a tomato-based sauce at least twice a week</li>\r\n</ul>', 'images/riskfactors/diet/goal.jpg'),
